import React, { useState } from 'react';
import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [movieDetails, setMoviesDetails] = useState([]);

  // const movieHandler = ()=>{
  //   fetch("https://swapi.dev/api/films/")
  //     .then((responce)=>{
