import React from 'react';
import AddUser from './Components/Users/AddUsers';
import DisplayItem from './Components/Users/DisplayItem';

function App() {
  let detail = [];
  const getUserItem = (details)=>{
    detail = [...details];
  }

  return (
    <div>
      <AddUser userList ={getUserItem} />
      <DisplayItem users= {detail} />
    </div>
  );
}

export default App;
