import React, { useState } from "react";
import Card from "../UI/Card";
import classes from './AddUsers.module.css'
import Button from "../UI/Button";

const AddUser = props=>{
    const [nmValue, setNm] = useState('');
    const [ageValue, setAge] = useState('');
    const submitHandler = (event)=>{
        event.preventDefault();
        setNm('');
        setAge('');
    };
    const nameHandler = (event)=>{
        setNm(event.target.value);
    }
    const ageHandler = (event)=>{
        setAge(event.target.value);
    }

    return(
        <div>
            <Card className={classes.input}>
                <form onSubmit={submitHandler}>
                    <label htmlFor="username" >User Name</label>
                    <input  type= "text" placeholder="enter name" id="username" onChange={nameHandler}/>
                    <label htmlFor="age">Age(year)</label>
                    <input  type= "number" placeholder="enter age" id="age" onChange={ageHandler}/>
                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        </div>
    );
};

export default AddUser;
